package com.arrayList;

public class Lec1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 10;    //primitive data type
		Integer x = 100; // no primitive data type
		System.out.println(a);
		System.out.println(x);
		
		a = x;
		// unboxing 
		System.out.println(a);
		
		x = a;
		// autoboxing
		System.out.println(x);
		
		
	}

}
